import React, {useState, useEffect} from 'react';

import ErrorMessages from 'sharedComponents/errorMessages';

const ArticleForm = ({onSubmit, errors, initialValues}) => {
    const [title, setTitle] = useState('');
    const [body, setBody] = useState('');
    const [description, setDescription] = useState('');
    const [tags, setTags] = useState('');

    const handlerSubmit = event => {
        event.preventDefault();
        const article = {
            title,
            body,
            description,
            tags
        };
        onSubmit(article);
    };

    useEffect(() => {
        if (!initialValues) return;
        setTitle(initialValues.title);
        setBody(initialValues.body);
        setDescription(initialValues.description);
        setTags(initialValues.tagList.join(' '));
    }, [initialValues]);

    return (
        <div className="editor-page">
            <div className="container-page">
                <div className="row">
                    <div className="col-md-10 offset-md-1 col-xs-12">
                        { errors && <ErrorMessages errors={errors} /> }
                        <form onSubmit={handlerSubmit}>
                            <fieldset>
                                <fieldset className="form-group">
                                    <input 
                                        type="text"
                                        className="form-control form-control-lg"
                                        placeholder="Article title" 
                                        value={title}
                                        onChange={e => setTitle(e.target.value)} />
                                </fieldset>
                                <fieldset className="form-group">
                                    <input 
                                        type="text"
                                        className="form-control form-control-lg"
                                        placeholder="What is this article about?"
                                        value={description}
                                        onChange={e => setDescription(e.target.value)} />
                                </fieldset>
                                <fieldset className="form-group">
                                    <textarea 
                                        className="form-control form-control-lg"
                                        placeholder="Write your article markdown ..."
                                        value={body}
                                        onChange={e => setBody(e.target.value)}>
                                    </textarea>
                                </fieldset>
                                <fieldset className="form-group">
                                    <input 
                                        type="text"
                                        className="form-control form-control-lg"
                                        placeholder="Enter tags" 
                                        value={tags}
                                        onChange={e => setTags(e.target.value)} />
                                </fieldset>
                                <fieldset className="form-group">
                                    <button className="btn btn-lg pull-xs-right btn-primary">Publish article</button>
                                </fieldset>
                            </fieldset>
                        </form>
                    </div>
                </div>
            </div>
        </div>
    )
};

export default ArticleForm;